mapper.select:
select count(1)
from tb_contract
where group_id = #{groupId}
  and uuid = #{uuid}

select contract_address
from tb_contract
where group_id = #{groupId}
  and uuid = #{uuid}

select contract_abi
from tb_contract
where group_id = #{groupId}
  and contract_address = #{contractAddress}

select 
from tb_transaction
where trans_status = 0

  and request_count <= #{requestCountMax}
  and TIMESTAMPDIFF(SECOND,gmt_modify,NOW()) >= #{intervalTime}

limit 0,#{selectCount}

select 
from tb_transaction
where trans_status = 0
and MOD(id,#{shardingTotalCount}) = #{shardingItem}

  and request_count <= #{requestCountMax}
  and TIMESTAMPDIFF(SECOND,gmt_modify,NOW()) >= #{intervalTime}

limit 0,#{selectCount}
